package org.sk.slidingwindow;

public class PermutationInString {

	public boolean checkInclusion(String s1, String s2) {
		int s1FreqArray[] = new int[26];

		int left = 0;
		int right = left;

		for (char c : s1.toCharArray()) {
			s1FreqArray[c - 97]++;
		}

		int totalChars = s1.length();
		while (right < s2.length()) {
			if (s1FreqArray[s2.charAt(right++) - 97]-- > 0) {
				totalChars--;
			}
			if (totalChars == 0) {
				return true;
			}
			// slide window
			if (right - left == s1.length() && s1FreqArray[s2.charAt(left++) - 97]++ >= 0) {
				totalChars++;
			}
		}
		return false;
	}

	public static void main(String[] args) {
		PermutationInString pis = new PermutationInString();
		System.out.println(pis.checkInclusion("ab", "eidbaooo"));
		System.out.println(pis.checkInclusion("ab", "eidboaoo"));
		System.out.println(pis.checkInclusion("adc", "dcda"));
		System.out.println(pis.checkInclusion("abc", "bbbca"));
		System.out.println(pis.checkInclusion("hello", "ooolleoooleh"));
		System.out.println(pis.checkInclusion("abc", "cccccbabbbaaaa"));
		System.out.println(pis.checkInclusion("mart", "karma"));
		System.out.println(pis.checkInclusion(
				"",
				""));
	}
}
